simulation.onInitCode:
var timeoutID = null;

this.clearWashingTimeout = _.bind(function(){
	if(timeoutID){
		clearTimeout(timeoutID);
		timeoutID = null;
	}
}, this);

this.startWashing = _.bind(function(){
	if((this.status !== "Ready" && this.status !== "Stopped") || this.doorOpen == "true")
		return;

  	var cyclesTimes = [7000, 5000, 5000];
	var cyclesStates = ["Wash", "Rinse", "Spin"];
	var cycleIndex = (this.status == "Stopped") ? 
        		cyclesStates.indexOf(this.currentCycle) : 0;

  	this.status = "Working";
 	var nextCycleFunc = _.bind(function(){
		this.currentCycle = cyclesStates[cycleIndex];
		var cycletime = cyclesTimes[cycleIndex];

		if(cyclesStates.length == cycleIndex){ //done
			this.currentCycle = "End";
			this.status = "Ready";
			timeoutID = null;
		}
		else{ //next cycle
			cycleIndex++;	
          	timeoutID = setTimeout(nextCycleFunc, cycletime);          	
        }
	}, this);

	// do start/resume washing
	nextCycleFunc();
}, this);

this.stopWashing = _.bind(function(){
	if(this.status !== "Working")
		return;//not working
	this.status = "Stopped";
	this.clearWashingTimeout();
}, this);

this.testSystem = _.bind(function(){
	var testPass = (Math.random() < 0.8);
	if(testPass){
      //this.failureType = null;
      return;
    }
	var failureTypes = ["Water Leak", "High Virbrations", "Board Malfunction"];
	var failureIndex = Math.floor(Math.random() * (failureTypes.length - 1)); 
	this.failureType = failureTypes[failureIndex];
	this.status = "Failure";
	this.clearWashingTimeout();
  	this.sendfailureAlertMessage();
}, this);

simulation.onMessageReceptionCode:
payload = (payload) ? JSON.parse(payload) : {};
switch(message){
  case "startWashing":
    this.startWashing();
    break;
  case "stopWashing":
    this.stopWashing();
}
